methodGetSelect.code:
public function getSelect(){
	$tab=$this->findAll();
	$tSelect=array();
	if($tab){
	foreach($tab as $oRow){
		$tSelect[ $oRow->#key# ]=$oRow->#val#;
	}
	}
	return $tSelect;
}

